import {useState, useEffect, useContext} from "react";
import {useParams} from "react-router-dom"
import Context from '../Contenxt'
import Button from 'react-bootstrap/Button';


const Detalle = () => {
  const [productDetail, setProductoDetail] = useState ({});
  const {tienda} = useContext(Context);
  const {id} = useParams();

  const obtenerDatos = () => { 
    const datosProducto = tienda.find((tienda) => tienda.id === id);
    setProductoDetail (datosProducto);
  };

  useEffect(() => {
    obtenerDatos();
  }, [tienda]);


  return (
    <>
    <div className ="container mt-5">
      <div className ="card-mb-3 estilos">
        <div className ="row g-0">
        <div className ="col-md-6">
          <img 
          src ={productDetail.img}
          className= "img-fluid estilos rounded-start"
          alt = "img1"
          />
        </div>
        <div className="col-md-6">
          <div className="card-body">
            <h5 className="card-title text-capitalize">{productDetail.name}</h5>
            <hr/>
             <p className="card-text">Estado: {productDetail.estado}</p>


           <div className="d-flex justify-content-around">
                    <h4>{productDetail.price}</h4> 
            </div>
            <Button 
                variant="danger"
                >AÃ±adir al carrito</Button>
          </div>
        </div>
        </div>

      </div>

    </div>

    </>
  )
}

export default Detalle
