import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import {useState, useEffect } from 'react';


import { AuthRouters } from './Routers/AuthRouters';
import { LogPublicRoutes } from './Routers/LogPublicRoutes';
import { PrivateR } from './Routers/PrivateR';
import { PrivateRoutes } from './Routers/PrivateRoutes';


import Context from './Contenxt';

function App() {
    const [tienda, setTienda] = useState([]);
    const [usuario, setUsuario] = useState([]);
    const [buscador, setBuscador] = useState("");


const isLogged = false


const getTienda = async () => {
    const res = await fetch ('./tienda.json')
    const data = await res.json()
    setTienda(data);
    console.log(data)

  };

  const getUsuarios = async () => {
    const res = await fetch ('./usuarios.json')
    const data = await res.json()
    setUsuario(data);
    console.log(data)

  };
  useEffect(() => {
    getUsuarios();
    getTienda();
  }, []);


  return (
    <div className="App">
      <Context.Provider value ={{tienda, setTienda, usuario, setUsuario, buscador, setBuscador}}>
        <BrowserRouter>
        <Routes>
         //rutas publicas 
         <Route path="/auth/*" element= {
           <LogPublicRoutes isLoggeg = {isLogged}>
             <AuthRouters/>
           </LogPublicRoutes>
         }/>
        //rutas privadas
         <Route path="/app/*" element= {
           <PrivateRotes isLoggeg = {isLogged}>
             <PrivateR/>
           </PrivateRotes>}/>
        </Routes>
        </BrowserRouter>
        </Context.Provider>


    </div>
  );
}

export default App;
