import "../index.css"
import NavBar from "../componentes/NavBar"
import Footer from '../componentes/Footer'
import Cards from "../componentes/Cards"
import React, { useContext } from 'react'
import Context from '../Context'


const Vestidos = () => {
  const [vestido, setVestido] = useState ({});
  const{tienda} = useContext (Context);

  const obtenerVestido = () => { 
    const datosDress = tienda.find((dress) => dress.categoria === vestido);
    setVestido (datosDress);
  };

  useEffect(() => {
    obtenerVestido();
  }, []);



  return (
    <>
      <NavBar/>

      <div className="container d-flex justify-content-center mt-3 mx-4">
        <h5 className="titleCat">Vestidos</h5>
      </div>
      <hr/>

      <div className="galeria grid-columns-4 p-3">


          <Cards key={vestido.id}/>



      </div>




      <Footer/>
    </>
  )
}

export default Vestidos
