import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import {useState, useEffect } from 'react';


import Registro from "./views/Registro";
import InicioSecion from './views/InicioSecion';
import Home from "./views/Home"
import MiPerfil from "./views/MiPerfil";
import Detalle from "./views/Detalle";
import Enteritos from "./views/Enteritos";
import Vestidos from "./views/Vestidos";
import Jardineras from "./views/Jardineras";
import Pantalon from "./views/Pantalon";
import Carrito from "./views/Carrito";
import CrearPub from "./views/CrearPub"
import { PrivateRoute } from './componentes/PrivateRoute';




import Context from './Contenxt';

function App() {
    const [tienda, setTienda] = useState([]);
    const [usuario, setUsuario] = useState([]);
    const [buscador, setBuscador] = useState("");
    const [auth,setAuth] = useState(null);
    const [newRegister, setNewRegister] = useState(usuario);

const getTienda = async () => {
    const res = await fetch ('./tienda.json')
    const data = await res.json()
    setTienda(data);
    //console.log(data)

  };

  const getUsuarios = async () => {
    const res = await fetch ('./usuarios.json')
    const data = await res.json()
    setUsuario(data);
    //console.log(data)

  };
  useEffect(() => {
    getUsuarios();
    getTienda();
  }, []);

  const agregarNuevo = (e) => {
    e.preventDefault();
    if (!nombre.trim() || !apellido.trim() || !correo.trim() || !clave.trim()){
      setError (true);
    }
    else {
    setNewRegister([...newRegister, { id: nanoid(), nombre: nombre, apellido: apellido, correo: correo, clave: clave}])
    setAuth({newRegister})
  }
  };
  console.log(newRegister);



  return (
    <div className="App">
      <Context.Provider 
      value ={{tienda, setTienda, 
              usuario, setUsuario, 
              buscador, setBuscador, 
              auth, setAuth,
              agregarNuevo}}>
        <BrowserRouter>
        <Routes>
        <Route index element={<InicioSecion/>} />
        <Route path="/iniciosesion" element={<InicioSecion/>} />
        <Route path="/registro" element={<Registro />} />
        <Route element={<PrivateRoute auth = {auth}/>}>
        <Route path="/home" element={<Home />} />
        <Route path="/carrito" element={<Carrito />} />
        <Route path="/vestidos" element={<Vestidos />}/>
        <Route path="/Enteritos" element={<Enteritos />} />
        <Route path="/Jardineras" element={<Jardineras />} />
        <Route path="/Pantalon" element={<Pantalon />} />
        <Route path="/miperfil" element={<MiPerfil />} />
        <Route path="/crearPub" element={<CrearPub />}/>
        </Route>

        <Route path="/detalle/:id" element={<Detalle />} />
        </Routes>
        </BrowserRouter>
        </Context.Provider>


    </div>
  );
}

export default App;
