import React, { useState, useEffect } from 'react'
import { Link } from "react-router-dom";
import "../assets/css/iniciosecion.css"

import logoN from "../assets/img/logoN.png"

const InicioSecion = () => {
  const [correo, setCorreo] = useState("");
  const [password, setPassword] = useState("");
  const [usuario, setUsuario] = useState([]);


  const getUsuarios = async () => {
    const res = await fetch ('../usuarios.json')
    const data = await res.json()
    setUsuario(data);
    console.log(data)

  };
  useEffect(() => {
    getUsuarios();
  }, []);

  return (

    <div className="bodyLogin">

      <div className="login">

        <div className="login-form">

          <img src={logoN} height="55" width="90" alt=""/>
          <br/>
          <p>Bienvenido a Marketplace</p>

          <form className="p-4">
            <div className="form-outline mb-4">
              <input type="email" 
             className="form-control"
             value={correo}
             onChange = {(e) => setCorreo(e.target.value)}
             />
              <label className="form-label" >Correo</label>
            </div>

            <div className="form-outline mb-4">
              <input type="password"
               className="form-control" 
               value={password}
               onChange = {(e) => setPassword(e.target.value)}/>
              <label className="form-label" >Contraseña</label>
            </div>

            <div className="row mb-4">
              <div className="col">
              <a href="#">¿Olvidaste tu contraseña?</a>
              </div>
            </div>
            <button type="submit" className="btn btn-block mb-4"><Link className="t"to="/Home">Ingresa a tu cuenta</Link></button>
            <div className="d-flex justify-content-around">
              <p>Aun no estas registrado?</p><Link to="/Registro">Registrate</Link>
            </div>
          </form> 
        </div>
      </div>

      <div className="nav p-4 justify-content-end">


      </div>


    </div>

  )
}

export default InicioSecion
